import re

from fsqa.readability.python import PythonChecker


class QtChecker(PythonChecker):
    readability = """
        ram*
    """.split()

    def __call__(self):
        if super(QtChecker, self).__call__():
            pyside_regex = re.compile("^((from)|(import)) [^\#]*(PySide)")
            return any(pyside_regex.match(l.strip()) for l in self.file_contents.split("\n"))
